import React, { Component } from "react";
import { Card, Button, Form, InputGroup, Col, Row } from "react-bootstrap";
import posed from "react-pose";

const Animation = posed.div({
  visible: { opacity: 1 },
  hidden: { opacity: 0 },
});

class AddUser extends Component {
  state = {
    visible: true,
  };

  changeVisibility = () => {
    this.setState({
      visible: !this.state.visible,
    });
  };

  render() {
    return (
      <Card className="text-center">
        <Card.Header>
          <div>
            <Row>
              <div className="col-md-8 text-center">
                <Card.Text as="h4">User add form</Card.Text>
              </div>
              <div className="col-md-2">
                <Button className="mr-1"
                  onClick={this.changeVisibility}
                  variant="outline-primary"
                  size="sm"
                >
                  {this.state.visible ? "Hide" : "Show"}
                </Button>
              </div>
            </Row>
          </div>
        </Card.Header>
        {this.state.visible? <Card.Body>
          <Form>
            <Form.Group controlId="nameInput">
              <Form.Label>Name : </Form.Label>
              <Form.Control type="name" placeholder="Gandalf Grihamut" />
              <Form.Text className="text-muted">
                Enter the name please.
              </Form.Text>
            </Form.Group>

            <Form.Group controlId="jobInput">
              <Form.Label>Job : </Form.Label>
              <Form.Control type="name" placeholder="White Wizard" />
              <Form.Text className="text-muted">
                Enter the job please.
              </Form.Text>
            </Form.Group>

            <Form.Group as={Col} md="8" controlId="validationCustomUsername">
              <Form.Label>Salary : </Form.Label>
              <InputGroup>
                <Form.Control
                  type="number"
                  placeholder="7000"
                  aria-describedby="inputGroupPrepend"
                  required
                />
                <InputGroup.Prepend>
                  <InputGroup.Text id="inputGroupPrepend">USD</InputGroup.Text>
                </InputGroup.Prepend>
              </InputGroup>
              <Form.Text className="text-muted">
                Enter a valid number please.
              </Form.Text>
            </Form.Group>
            <Button className="btn btn-block" variant="primary" type="submit">
              Save
            </Button>
          </Form>
        </Card.Body> :null }

      </Card>
    );
  }
}
export default AddUser;

