import React, { Component } from "react";
import UserConsumer from "../context";
import { Button } from "react-bootstrap";
import axios from "axios";
import Popup from "reactjs-popup";

class ListItemC extends Component {
  deleteUser = async (dispatch, e) => {
    const { id } = this.props;

    console.log(id);
    await axios.delete(`http://localhost:3004/users/${id}`);

    dispatch({
      type: "DELETE_USER",
      payload: id,
    });
  };

  render() {
    return (
      <UserConsumer>
        {(value) => {
          return (
            <tr>
              <td>{this.props.id}</td>
              <td>{this.props.name}</td>
              <td>{this.props.jobTitle}</td>
              <td>{this.props.salary}</td>
              <td>
                <Popup trigger={<Button variant="warning"><i class="fas fa-user-edit"></i></Button>} position="right center">
                  <div>Popup content here !!</div>
                </Popup>
              </td>
              <td>
                <Button
                  onClick={this.deleteUser.bind(this, value.dispatch)}
                  variant="danger"
                >
                  <i className="far fa-trash-alt"></i>
                </Button>
              </td>

            </tr>
          );
        }}
      </UserConsumer>
    );
  }
}
export default ListItemC;
