import React, { Component } from "react";
import {
  Navbar,
  Table,
  InputGroup,
  FormControl,
  Button,
} from "react-bootstrap";
import UserConsumer from "../context";
import axios from "axios";


class BottomAdd extends Component {

    addUser = async (dispatch, e) => {
        e.preventDefault();
        const {name,jobTitle,salary} = this.state;
        const newUser = {
          name,
          jobTitle,
          salary,
        };
    const response = await axios.post("http://localhost:3004/users",newUser)

        dispatch({
          type: "ADD_USER",
          payload: response.data,
        });
      };

  render() {
    return (
      <UserConsumer>
        {(value) => {
          return (
            <div className="container container-fluid">
              <Navbar className="fixed-bottom">
                <Table
                  responsive
                  bordered
                  hover
                  className="text-center"
                  size="md"
                  style={{ marginBottom: "1px" }}
                >
                  <tbody>
                    <div className="form-group" onSubmit={this.addUser}>
                      <tr>
                        <td>
                          <InputGroup>
                            <FormControl
                              placeholder="Username"
                              aria-label="Username"
                              aria-describedby="basic-addon1"
                            />
                          </InputGroup>
                        </td>
                        <td>
                          <InputGroup>
                            <FormControl
                              placeholder="Job"
                              aria-label="Job"
                              aria-describedby="basic-addon1"
                            />
                          </InputGroup>
                        </td>
                        <td>
                          <InputGroup>
                            <FormControl
                              type="number"
                              placeholder="Salary"
                              aria-label="Salary"
                              aria-describedby="basic-addon1"
                            />
                          </InputGroup>
                        </td>
                        <td>
                          <Button
                            className="btn"
                            variant="primary"
                            type="submit"
                          >
                            <i className="fas fa-user-plus"></i>
                          </Button>
                        </td>
                      </tr>
                    </div>
                  </tbody>
                </Table>
              </Navbar>
            </div>
          );
        }}
      </UserConsumer>
    );
  }
}
export default BottomAdd;
