import React, { Component } from "react";
import { Navbar, Row, Col, Form, Button } from "react-bootstrap";
import UserConsumer from "../context";
import axios from "axios";

class BottomAdd extends Component {
  state = {
    name: "",
    jobTitle: "",
    salary: "",
  };

  addUser = async (dispatch, e) => {
    e.preventDefault();
    const { name, jobTitle, salary } = this.state;
    const newUser = {
      name,
      jobTitle,
      salary,
    };
    const response = await axios.post("http://localhost:3004/users", newUser);

    dispatch({
      type: "ADD_USER",
      payload: response.data,
    });
  };
  addName = (e) => {
    this.setState({ name: e.target.value });
  };
  addJob = (e) => {
    this.setState({ jobTitle: e.target.value });
  };
  addSalary = (e) => {
    this.setState({ salary: e.target.value });
  };
  render() {
    return (
      <UserConsumer>
        {(value) => {
          return (
            <div className="container container-fluid">
              <Navbar className="fixed-bottom text-center">
                <Form onSubmit={this.addUser}>
                  <Row>
                    <Col>
                      <Form.Group value={this.state.salary}
                      onChange={this.addSalary}
                      as={Col}
                      md="4"
                      controlId="salary">
                        <Form.Control placeholder="First name" />
                      </Form.Group>
                    </Col>
                    <Col>
                      <Form.Group value={this.state.salary}
                      onChange={this.addSalary}
                      as={Col}
                      md="4"
                      controlId="salary">
                        <Form.Control placeholder="Last name" />
                      </Form.Group>
                    </Col>
                    <Col>
                      <Form.Group value={this.state.salary}
                      onChange={this.addSalary}
                      as={Col}
                      md="3"
                      controlId="salary">
                        <Form.Control placeholder="First name" />
                      </Form.Group>
                    </Col>
                    <Form.Group value={this.state.salary}
                      onChange={this.addSalary}
                      as={Col}
                      md="1"
                      controlId="salary">
                      <Button variant="primary" type="submit">
                        Submit
                      </Button>
                    </Form.Group>
                  </Row>
                </Form>
              </Navbar>
            </div>
          );
        }}
      </UserConsumer>
    );
  }
}
export default BottomAdd;
