import React, { Component } from "react";
import {
  Container,
  Form,
  Card,
  Button,
  Row,
  InputGroup,
  Col,
} from "react-bootstrap";
import UserConsumer from "../context";
import axios from "axios";

class UpdateUser extends Component {
  state = {
    name: "",
    jobTitle: "",
    salary: "",
  };

  componentDidMount = async () => {
    const { id } = this.props.match.params;
    console.log(id);

    const response = await axios.get(`http://localhost:3004/users/${id}`);
    const { name, jobTitle, salary } = response.data;
    this.setState({
      name,
      jobTitle,
      salary,
    });
  };

  updateUser = async (dispatch, e) => {
    e.preventDefault();
    const { id } = this.props.match.params;
    const { name, jobTitle, salary } = this.state;
    const updatedUser = {
      name,
      salary,
      jobTitle,
    };
    console.log(updatedUser);
    const response = await axios.put(
      `http://localhost:3004/users/${id}`,
      updatedUser
    );

    dispatch({ type: "UPDATE_USER", payload: response.data });
  };
  addName = (e) => {
    this.setState({ name: e.target.value });
  };
  addJob = (e) => {
    this.setState({ jobTitle: e.target.value });
  };
  addSalary = (e) => {
    this.setState({ salary: e.target.value });
  };

  render() {
    return (
      <UserConsumer>
        {(value) => {
          return (
            <Container className="col-md-4">
              <Card className="text-center">
                <Card.Header id="header">
                  <div>
                    <Row>
                      <div className="col-md-9 text-center">
                        <Card.Text as="h4">Update User form</Card.Text>
                      </div>
                      <div className="col-md-2"></div>
                    </Row>
                  </div>
                </Card.Header>

                <Card.Body>
                  <Form onSubmit={this.updateUser.bind(this, value.dispatch)}>
                    <Form.Group  value={this.state.name}
                        onChange={this.addName}>
                      <Form.Label>Name : </Form.Label>
                      <Form.Control
                        type="name"

                      />
                      <Form.Text className="text-muted">
                        Enter the name please.
                      </Form.Text>
                    </Form.Group>

                    <Form.Group>
                      <Form.Label>Job : </Form.Label>
                      <Form.Control
                        type="name"
                        value={this.state.jobTitle}
                        onChange={this.addJob}
                      />
                      <Form.Text className="text-muted">
                        Enter the job please.
                      </Form.Text>
                    </Form.Group>

                    <Form.Group as={Col} md="8" controlId="salary">
                      <Form.Label>Salary : </Form.Label>
                      <InputGroup>
                        <Form.Control
                          value={this.state.salary}
                          onChange={this.addSalary}
                          type="number"
                          required
                        />
                      </InputGroup>
                      <Form.Text className="text-muted">
                        Enter a valid number please.
                      </Form.Text>
                    </Form.Group>
                    <Button
                      className="btn btn-block"
                      variant="primary"
                      type="submit"
                    >
                      Save
                    </Button>
                  </Form>
                </Card.Body>
              </Card>
            </Container>
          );
        }}
      </UserConsumer>
    );
  }
}
export default UpdateUser;
