import React, { Component } from "react";
import { Navbar, Row, Col, Form, InputGroup } from "react-bootstrap";
import UserConsumer from "../context";
import axios from "axios";

class BottomAdd extends Component {
  state = {
    name: "",
    jobTitle: "",
    salary: "",
  };

  addUser = async (dispatch, e) => {
    e.preventDefault();
    const { name, jobTitle, salary } = this.state;
    const newUser = {
      name,
      jobTitle,
      salary,
    };
    const response = await axios.post("http://localhost:3004/users", newUser);

    dispatch({
      type: "ADD_USER",
      payload: response.data,
    });
  };
  addName = (e) => {
    this.setState({ name: e.target.value });
  };
  addJob = (e) => {
    this.setState({ jobTitle: e.target.value });
  };
  addSalary = (e) => {
    this.setState({ salary: e.target.value });
  };
  render() {
    return (
      <UserConsumer>
        {(value) => {
          return (
            <div className="container container-fluid">
              <Navbar className="fixed-bottom text-center">
                <Form onSubmit={this.addUser}>
                  <Row>
                    <Col>
                    <Form.Group
                      value={this.state.name}
                      onChange={this.addName}
                      controlId="name"
                    >
                      <Form.Label>Name : </Form.Label>
                      <Form.Control
                        type="name"
                        placeholder="Gandalf Grihamut"
                      />
                      <Form.Text className="text-muted">
                        Enter the name please.
                      </Form.Text>
                    </Form.Group>
                    </Col>
                    <Col>
                    <Form.Group
                      value={this.state.name}
                      onChange={this.addName}
                      controlId="name"
                    >
                      <Form.Label>Name : </Form.Label>
                      <Form.Control
                        type="name"
                        placeholder="Gandalf Grihamut"
                      />
                      <Form.Text className="text-muted">
                        Enter the name please.
                      </Form.Text>
                    </Form.Group>
                    </Col>
                    <Col>
                    <Form.Group
                      value={this.state.salary}
                      onChange={this.addSalary}
                      as={Col}
                      md="8"
                      controlId="salary"
                    >
                      <Form.Label>Salary : </Form.Label>
                      <InputGroup>
                        <Form.Control
                          type="number"
                          placeholder="7000"
                          aria-describedby="inputGroupPrepend"
                          required
                        />
                        <InputGroup.Prepend>
                          <InputGroup.Text id="inputGroupPrepend">
                            USD
                          </InputGroup.Text>
                        </InputGroup.Prepend>
                      </InputGroup>
                      <Form.Text className="text-muted">
                        Enter a valid number please.
                      </Form.Text>
                    </Form.Group>
                    </Col>
                  </Row>
                </Form>
              </Navbar>
            </div>
          );
        }}
      </UserConsumer>
    );
  }
}
export default BottomAdd;
