import React, { Component } from "react";
import { Navbar, Row, Col, Form, Button,Container } from "react-bootstrap";
import UserConsumer from "../context";
import axios from "axios";

class BottomAdd extends Component {
  state = {
    name: "",
    jobTitle: "",
    salary: "",
  };

  addUser = async (dispatch, e) => {
    e.preventDefault();
    const { name, jobTitle, salary } = this.state;
    const newUser = {
      name,
      jobTitle,
      salary,
    };
    const response = await axios.post("http://localhost:3004/users", newUser);

    dispatch({
      type: "ADD_USER",
      payload: response.data,
    });
  };
  addName = (e) => {
    this.setState({ name: e.target.value });
  };
  addJob = (e) => {
    this.setState({ jobTitle: e.target.value });
  };
  addSalary = (e) => {
    this.setState({ salary: e.target.value });
  };
  render() {
    return (
      <UserConsumer>
        {(value) => {
          return (
            <Container className="container-fluid">
              <Navbar className="fixed-bottom text-center">
                <div className="text-center col-md-12">

                  <Form onSubmit={this.addUser}>
                    <Row>
                      <Col>
                        <Form.Group
                          value={this.state.name}
                          onChange={this.addName}
                          controlId="name"
                        >
                          <Form.Control type="name" placeholder="Enter name" />
                        </Form.Group>
                      </Col>
                      <Col>
                        <Form.Group
                          value={this.state.jobTitle}
                          onChange={this.addJob}
                          controlId="jobTitle"
                        ><Form.Text className="text-muted">
                        Enter a valid number please.
                      </Form.Text>
                          <Form.Control
                            type="name"
                            placeholder="Enter the job"
                          />
                        </Form.Group>
                      </Col>
                      <Col>
                        <Form.Group
                          value={this.state.salary}
                          onChange={this.addName}
                          controlId="name"
                        >
                          <Form.Control
                            type="number"
                            placeholder="Enter the salary"
                          />
                        </Form.Group>
                      </Col>
                      <Col>
                        <Button className="btn" variant="primary" type="submit">
                          Save
                        </Button>
                      </Col>
                    </Row>
                  </Form>
                </div>
              </Navbar>
            </Container>
          );
        }}
      </UserConsumer>
    );
  }
}
export default BottomAdd;
