import React, { Component } from "react";
import {
  Navbar,
  Table,
  InputGroup,
  FormControl,
  Form,
  Button,
} from "react-bootstrap";
import UserConsumer from "../context";
import axios from "axios";

class BottomAdd extends Component {
  state = {
    name: "",
    jobTitle: "",
    salary: "",
  };

  addUser = async (dispatch, e) => {
    e.preventDefault();
    const { name, jobTitle, salary } = this.state;
    const newUser = {
      name,
      jobTitle,
      salary,
    };
    const response = await axios.post("http://localhost:3004/users", newUser);

    dispatch({
      type: "ADD_USER",
      payload: response.data,
    });
  };
  addName = (e) => {
    this.setState({ name: e.target.value });
  };
  addJob = (e) => {
    this.setState({ jobTitle: e.target.value });
  };
  addSalary = (e) => {
    this.setState({ salary: e.target.value });
  };
  render() {
    return (
      <UserConsumer>
        {(value) => {
          return (
            <div className="container container-fluid">
              <Navbar className="fixed-bottom">
                <Table
                  responsive
                  bordered
                  hover
                  className="text-center"
                  size="md"
                  style={{ marginBottom: "1px" }}
                >
                  <tbody>
                    <Form.Group onSubmit={this.addUser}>
                      <tr>
                        <td>
                          <InputGroup>
                            <FormControl
                              placeholder="Name"
                              aria-label="Name"
                              aria-describedby="basic-addon1"
                              value={this.state.name}
                              onChange={this.addName}
                            />
                          </InputGroup>
                        </td>
                        <td>
                          <InputGroup>
                            <FormControl
                              placeholder="Job"
                              aria-label="Job"
                              aria-describedby="basic-addon1"
                              value={this.state.jobTitle}
                              onChange={this.addJob}
                            />
                          </InputGroup>
                        </td>
                        <td>
                          <InputGroup>
                            <FormControl
                              type="number"
                              placeholder="Salary"
                              aria-label="Salary"
                              aria-describedby="basic-addon1"
                              value={this.state.addSalary}
                              onChange={this.addSalary}
                            />
                          </InputGroup>
                        </td>
                        <td>
                          <Button
                            className="btn"
                            variant="primary"
                            type="submit"
                          >
                            <i className="fas fa-user-plus"></i>
                          </Button>
                        </td>
                      </tr>
                    </Form.Group>
                  </tbody>
                </Table>
              </Navbar>
            </div>
          );
        }}
      </UserConsumer>
    );
  }
}
export default BottomAdd;
