import React, { Component } from "react";
import { Navbar, Row, Col, Form, Button, Nav } from "react-bootstrap";
import UserConsumer from "../context";
import axios from "axios";

class BottomAdd extends Component {
  constructor(props) {
    super(props);

    this.toggleNavbar = this.toggleNavbar.bind(this);

    this.state = {
      collapsed: true,
      name: "",
      jobTitle: "",
      salary: "",
    };
  }
  addUser = async (dispatch, e) => {
    e.preventDefault();
    const { name, jobTitle, salary } = this.state;
    const newUser = {
      name,
      jobTitle,
      salary,
    };
    const response = await axios.post(
      "https://infinite-escarpment-57092.herokuapp.com/users",
      newUser
    );

    dispatch({
      type: "ADD_USER",
      payload: response.data,
    });
  };
  toggleNavbar() {
    this.setState({
      collapsed: !this.state.collapsed,
    });
  }

  addName = (e) => {
    this.setState({ name: e.target.value });
  };
  addJob = (e) => {
    this.setState({ jobTitle: e.target.value });
  };
  addSalary = (e) => {
    this.setState({ salary: e.target.value });
  };
  render() {
    return (
      <UserConsumer>
        {(value) => {
          const collapsed = this.state.collapsed;
          return (
            <Navbar
              className="text-center"
              collapseOnSelect
              expand="lg"
              bg="dark"
              variant="dark"
            >
              {collapsed ? (
                <div>
                  <Navbar.Brand>Add Employee Easily</Navbar.Brand>
                  <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                  <Navbar.Collapse id="responsive-navbar-nav" id="collapse1">
                    <Nav>
                      <Form
                        className="text-center"
                        onSubmit={this.addUser.bind(this, value.dispatch)}
                      >
                        {" "}
                        <Form.Group
                          value={this.state.name}
                          onChange={this.addName}
                          controlId="name"
                        >
                          <Form.Text className="text-muted">
                            Enter the name please.
                          </Form.Text>
                          <Form.Control
                            type="name"
                            placeholder="Gandalf the Grey"
                          />
                        </Form.Group>
                        <Form.Group
                          value={this.state.jobTitle}
                          onChange={this.addJob}
                          controlId="jobTitle"
                        >
                          <Form.Text className="text-muted">
                            Enter the job please.
                          </Form.Text>
                          <Form.Control
                            type="name"
                            placeholder="White Wizard"
                          />
                        </Form.Group>
                        <Form.Group
                          value={this.state.salary}
                          onChange={this.addSalary}
                          controlId="name"
                        >
                          <Form.Text className="text-muted">
                            Enter the salary please.
                          </Form.Text>
                          <Form.Control type="number" placeholder="7000" />
                        </Form.Group>{" "}
                        <Form.Text className="text-muted">
                          Clik to add user.
                        </Form.Text>
                        <Button
                          className="btn col-md-3 ml-auto mr-auto"
                          variant="primary"
                          type="submit"
                        >
                          <i className="fas fa-user-plus"></i>
                        </Button>
                      </Form>
                    </Nav>
                  </Navbar.Collapse>
                </div>
              ) : (
                <div>
                  <Navbar.Brand>Add Employee Easily</Navbar.Brand>
                  <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                  <Navbar.Collapse id="responsive-navbar-nav" id="collapse">
                    <Nav>
                      <Form
                        className="text-center"
                        onSubmit={this.addUser.bind(this, value.dispatch)}
                      >
                        <Row>
                          {" "}
                          <Col>
                            <Form.Group
                              value={this.state.name}
                              onChange={this.addName}
                              controlId="name"
                            >
                              <Form.Text className="text-muted">
                                Enter the name please.
                              </Form.Text>
                              <Form.Control
                                type="name"
                                placeholder="Gandalf the Grey"
                              />
                            </Form.Group>
                          </Col>
                          <Col>
                            <Form.Group
                              value={this.state.jobTitle}
                              onChange={this.addJob}
                              controlId="jobTitle"
                            >
                              <Form.Text className="text-muted">
                                Enter the job please.
                              </Form.Text>
                              <Form.Control
                                type="name"
                                placeholder="White Wizard"
                              />
                            </Form.Group>
                          </Col>
                          <Col>
                            <Form.Group
                              value={this.state.salary}
                              onChange={this.addSalary}
                              controlId="name"
                            >
                              <Form.Text className="text-muted">
                                Enter the salary please.
                              </Form.Text>
                              <Form.Control type="number" placeholder="7000" />
                            </Form.Group>
                          </Col>
                          <Col>
                            {" "}
                            <Form.Text className="text-muted">
                              Clik to add user.
                            </Form.Text>
                            <Button
                              className="btn col-md-3 ml-auto mr-auto"
                              variant="primary"
                              type="submit"
                            >
                              <i className="fas fa-user-plus"></i>
                            </Button>
                          </Col>
                        </Row>
                      </Form>
                    </Nav>
                  </Navbar.Collapse>
                </div>
              )}
            </Navbar>
          );
        }}
      </UserConsumer>
    );
  }
}
export default BottomAdd;
