import React, { Component } from "react";
import { Card, Button, Form, InputGroup, Col, Row } from "react-bootstrap";
var uniqid = require("uniqid");

class AddUser extends Component {
  state = {
    visible: true,
    name: "",
    jobTitle: "",
    salary: "",
  };

  changeVisibility = () => {
    this.setState({
      visible: !this.state.visible,
    });
  };

  addUser = (e) => {
    e.preventDefault();
    const newUser = {
      id: uniqid(),
      name: this.state.name,
      jobTitle: this.state.jobTitle,
      salary: this.state.salary,
    };
    console.log(newUser);
  };

  render() {
    return (
      <Card className="text-center">
        <Card.Header id="header">
          <div>
            <Row>
              <div className="col-md-10 text-center">
                <Card.Text as="h4">User add form</Card.Text>
              </div>
              <div className="col-md-2">
                <Button
                  onClick={this.changeVisibility}
                  variant="outline-primary"
                  size="sm"
                >
                  {this.state.visible ? "Hide" : "Show"}
                </Button>
              </div>
            </Row>
          </div>
        </Card.Header>
        {this.state.visible ? (
          <Card.Body>
            <Form onSubmit={this.addUser}>
              <Form.Group controlId="name">
                <Form.Label>Name : </Form.Label>
                <Form.Control name="name" type="name" placeholder="Gandalf Grihamut" />
                <Form.Text className="text-muted">
                  Enter the name please.
                </Form.Text>
              </Form.Group>

              <Form.Group controlId="jobTitle">
                <Form.Label>Job : </Form.Label>
                <Form.Control type="name" placeholder="White Wizard" />
                <Form.Text className="text-muted">
                  Enter the job please.
                </Form.Text>
              </Form.Group>

              <Form.Group as={Col} md="8" controlId="salary">
                <Form.Label>Salary : </Form.Label>
                <InputGroup>
                  <Form.Control
                    type="number"
                    placeholder="7000"
                    aria-describedby="inputGroupPrepend"
                    required
                  />
                  <InputGroup.Prepend>
                    <InputGroup.Text id="inputGroupPrepend">
                      USD
                    </InputGroup.Text>
                  </InputGroup.Prepend>
                </InputGroup>
                <Form.Text className="text-muted">
                  Enter a valid number please.
                </Form.Text>
              </Form.Group>
              <Button className="btn btn-block" variant="primary" type="submit">
                Save
              </Button>
            </Form>
          </Card.Body>
        ) : null}
      </Card>
    );
  }
}
export default AddUser;

