import React, { Component } from "react";
import { Card, Button } from "react-bootstrap";
import UserConsumer from "../context";
import axios from "axios"

export default class User extends Component {
  state = {
    test: "State'in test verisi",
    isVisible: false,
  };

  onClickEvent = (e) => {
    this.setState({
      isVisible: !this.state.isVisible,
    });
  };
  deleteUser = async (dispatch, e) => {
    const { id } = this.props;

    await axios.delete(`http://localhost:3004/users/${id}`);

    dispatch({
      type: "DELETE_USER",
      payload: id,
    });
  };

  //render fonksiyonu componentten miras adlığı için render içindeki
  //thisler User.js'i temsil ediyor ancak render dışında kendi yazdığımız
  //fonksiyonlar onClickEvent() gibi, onlarda this hiçbir şey ifade etmiyor
  //User.js'i ifade edebilmesi için .bind(this) yapmamız gerekir.

  //ama arrow function olarak yazdıysan binda gerek yok
  render() {
    return (
      <UserConsumer>
        {(value) => {
          return (
            <div className="mb-3 mt-2">
              <Table responsive bordered hover>
  <thead>
    <tr>
      <th>#</th>
      <th>Name</th>
      <th>Job</th>
      <th>Salary</th>
      <th></th>
    </tr>
  </thead>
  <tbody>
    <tr>
      <td>{this.props.id}</td>
      <td>{this.props.name}</td>
      <td>{this.props.jobTitle}</td>
      <td>{this.props.salary}</td>
      <th><Button
                      onClick={this.deleteUser.bind(this, value.dispatch)}
                      variant="danger"
                    >
                      <i className="far fa-trash-alt"></i>
                    </Button></th>
    </tr>
  </tbody>
</Table>
            </div>
          );
        }}
      </UserConsumer>
    );
  }
}

